INSERT INTO nuomi_article_category VALUES ('10', '公司简介', '', '', '', '<div><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;智卡众筹是广东智卡车联网服务有限公司在互联网上的二手车中介服务平台。&nbsp;</strong></span></div><div><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;本公司立于2016年8月，公司位于佛山市南海区中金路1号中国现代化企业绿色基地的总部（简称：中企绿色总部），誓要打造珠三角最大的专业汽车众筹平台，智卡众筹平台与优秀车商，精品二手车与优秀投资人相结合，最终实现互联网金融全面发展。</strong></span></div><div><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;智卡众筹平台本着“诚信，透明，安全，共赢”的经营理念，将致力打造一个稳健，专业，高效的一站式汽车众筹服务平台。</strong></span></div><div><span style=\"font-size:18px;\"><strong>&nbsp; &nbsp;公司秉承以客户为中心，用服务做保障的经营理念，希望通过我们的努力，严格审核每一台众筹的汽车，做好每一台众筹汽车的售前及售后服务，以最短的时间最优的价格完成销售，保障用户的稳定收益，为二手车行业的不断发展添砖加瓦，为广大投资用户提供最优质的互联网汽车金融服务，全力打造一个安全，高效，诚信，互利共赢的互联网信息服务平台。</strong></span></div><div><span style=\"font-size:18px;\"><strong>智卡众筹互联网金融平台的全体员工希望在新征程里与您，相互信任，齐心协力，携手共创辉煌的明天！&nbsp;</strong></span></div>', '9', '0', '8', 'intro', '0', '1389931247', '0', 'article');
